import React, { useState } from "react";
import "./Input.css";
import ColorSelect from "../ColorSelect/ColorSelect";

const Input = ({ message, setMessage, sendMessage }, props) => {
  const [color, setColor] = useState('#2979FF');

  const colorChange = (newColor) => {
    setColor(newColor);
    props.colorChange(color);
  }

  return (
    <div>
      <ColorSelect onColorSelectClick={colorChange} />
      <form className="form">
        <input
          className="input"
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(event) => setMessage(event.target.value)}
          onKeyPress={(event) =>
            event.key === "Enter" ? sendMessage(event) : null
          }
        />
        <button className="sendButton" style={{ backgroundColor: color}} onClick={(event) => sendMessage(event)}>
          Send
        </button>
      </form>
    </div>
  );
};

export default Input;
