import { handleResponse } from "./api";

const API_BASE_URL = 'https://react-chat-app-alicia.herokuapp.com/';
const API_REGISTER = 'register';
const API_LOGIN = 'login';
const API_USERS = 'users';


const createFetchOptions = (method, body) => ({
    method: method,
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(body)
  });

export const registerUser = (username, password) => {
    return fetch(
        API_BASE_URL + API_REGISTER, 
        createFetchOptions('POST', {
                username, 
                password 
            }
        )
    ).then(r => r.json())
    .then(handleResponse);
}

export const loginUser = (username, password) => {
    return fetch(
        API_BASE_URL + API_LOGIN, 
        createFetchOptions('POST', {
                username, 
                password 
            }
        )
    ).then(r => r.json())
    .then(handleResponse);
}

export const fetchUsers = () => {
    return fetch(
        API_BASE_URL + API_USERS, { method: 'GET', 'Content-Type': 'application/json'}
    ).then(r => r.json())
    .then(handleResponse);
}
