import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import queryString from 'query-string';
import io from 'socket.io-client';

let socket;


import './Join.css';

const Join = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [room, setRoom] = useState('chatroom');

    const checkCredentials = ( { location } ) => {
        const { name, password, room } = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setPassword(password);
        setRoom(room);

        socket.emit('login', { name, password, room }, () => {
            history.replace(`/chat?name=${name}&room=chatroom`);
        });
    }

    return (
        <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join</h1>
                <div><input placeholder="Name" className="joinInput" type="text" onChange={(event) => setName(event.target.value)} /></div>
                <div><input placeholder="Password" className="joinInput" type="password" onChange={(event) => setPassword(event.target.value)} /></div>
                <Link onClick={(event) => (!name || !password) ? event.preventDefault() : null} to={checkCredentials}>
                    <button className="button mt-20" type="submit">Sign In</button>
                </Link>
                {/* <Link onClick={(event) => (!name || !password) ? event.preventDefault() : null} to={`/chat?name=${name}&password=${password}&room=chatroom`}>
                    <button className="button mt-20" type="submit">Sign In</button>
                </Link> */}
            </div>
        </div>
    );
}

export default Join;
