import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import queryString from 'query-string';
import io from 'socket.io-client';
import './Join.css';

let socket;


const Join = ({ location }) => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [room, setRoom] = useState('chatroom');
    const history = useHistory();
    const ENDPOINT = 'localhost:5000';

    const checkCredentials = () => {

        if (password && name) {
            socket = io(ENDPOINT);

            socket.emit('login', { name, password, room }, (result) => {
                if(result.error) {
                    console.log(result);
                } else {
                    history.replace(`/chat?name=${name}&room=chatroom`);
                }
            });
        }  
    }

    return (
        <div className="joinOuterContainer">
            <div className="joinInnerContainer">
                <h1 className="heading">Join</h1>
                <div><input placeholder="Name" className="joinInput" type="text" onChange={(event) => setName(event.target.value)} /></div>
                <div><input placeholder="Password" className="joinInput" type="password" onChange={(event) => setPassword(event.target.value)} /></div>
                    <button className="button mt-20" type="button" onClick={checkCredentials}>Sign In</button>
                {/* <Link onClick={(event) => (!name || !password) ? event.preventDefault() : null} to={`/chat?name=${name}&password=${password}&room=chatroom`}>
                    <button className="button mt-20" type="submit">Sign In</button>
                </Link> */}
            </div>
        </div>
    );
}

export default Join;
